package com.evergent.corejava.GeneralProgramming;

public class BiggestOfThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,c=30;
		if(a>b && a>c) {
			System.out.print(a);
		}
		else if (b>a && b>c) {
			System.out.print(b);
		}
		else {
			System.out.print(c);
		}

	}

}
